INSERT INTO `T_Art17_FLAAgglomeration` (`aggCode`, `aggName`, `Art17_FLAaggloID`, `flaggComments`, `flaggEUFund`, `flaggEUFundName`, `flaggExpecDateCompletion`, `flaggExpecDateStart`, `flaggExpecDateStartWork`, `flaggExpLoad`, `flaggExpLoadColl`, `flaggExpLoadIAS`, `flaggInv`, `flaggLoan`, `flaggLoanName`, `flaggMeasures`, `flaggOtherFund`, `flaggOtherFundName`, `flaggReasons`, `flaggStatus`, `flarepCode`) VALUES ("IT1987037_01","RAMACCA",240354,"Agglomerato in procedura d'infrazione 2014/2059. In Attesa DPCM ex art.4 septies c.4  del D.L.n.32 del 18.04.2019, conv.con L.55/2019. Il progeto complessivo di un importo di 3,00 M€ prevede sia un intervento che interessa l'impianto fognario sia un intervento che interessa l'impianto di depurazione. L'importo di previsto troverà copertura finanziaria per 0,30 M€ sull'FSC 2014/2020 (PATTO PER IL SUD) il restante importo troverà copertura finanziaria non appena l'intervento passerà al commissario unico per la depurazione..",NULL,NULL,"12/31/23 00:00:00","06/30/21 00:00:00","01/01/22 00:00:00",NULL,NULL,NULL,1.5000000000,NULL,NULL,"Ripristino e completamento della rete fognaria",NULL,NULL,"Sebbene il 100% del carico sia collettato,  la rete fognaria necessita di ristrutturazione ed il depuratore non è adeguato ed  il trattamento dei reflui all'ID non raggiunge la conformità","NC","IT007");
