INSERT INTO `T_Art17_FLAAgglomeration` (`aggCode`, `aggName`, `Art17_FLAaggloID`, `flaggComments`, `flaggEUFund`, `flaggEUFundName`, `flaggExpecDateCompletion`, `flaggExpecDateStart`, `flaggExpecDateStartWork`, `flaggExpLoad`, `flaggExpLoadColl`, `flaggExpLoadIAS`, `flaggInv`, `flaggLoan`, `flaggLoanName`, `flaggMeasures`, `flaggOtherFund`, `flaggOtherFundName`, `flaggReasons`, `flaggStatus`, `flarepCode`) VALUES ("UKENTH_Agg000014","BECKTON",240567,"Lee Tunnel is now operational.Construction of the 80 metre deep shaft at Beckton began in September 2010. In 2012, construction of the 7 m diameter tunnel started, the tunneling process was completed in January 2014. The Lee Tunnel was commissioned on 28 January 2016. Financial support for Tideway Tunnel in place and site preparation beginning. Investment figures are in Euros based on a 1 £ sterling to 1.13 Euros conversion.",0.0000000000,"na","07/01/23 00:00:00","12/01/06 00:00:00","04/01/10 00:00:00",2920685,100.0000000000,0.0000000000,6513.0000000000,NULL,NULL,"Improved sewage treatment at the works both in terms of the volume that can be treated and the quality of the effluent. Over 55% expansion, new permit 2 years ago. Lee Tunnel construction to capture the flows that would previously have discharged to river from the Abbey Mills pumping station and conveying them to Beckton Sewage Treatment Works for full treatment. Tideway Tunnel construction to capture the flows that would previously have discharged to river and conveying them to Beckton Sewage Treatment Works for full treatment. The Tideway tunnel has continued to make good progress on constructing the tunnel with the construction activity is now over 47% complete, and 6km of the 25km main tunnel now complete. Four tunnel boring machines (TBMs) have been launched, with another two to launch in 2020. Furthermore, 20 of the 23 shafts are in progress, with nine now at full depth",NULL,NULL,"Significant proportion of effluent not passing through full treatment instead discharging via storm overflows","NC","UK5");
INSERT INTO `T_Art17_FLAAgglomeration` (`aggCode`, `aggName`, `Art17_FLAaggloID`, `flaggComments`, `flaggEUFund`, `flaggEUFundName`, `flaggExpecDateCompletion`, `flaggExpecDateStart`, `flaggExpecDateStartWork`, `flaggExpLoad`, `flaggExpLoadColl`, `flaggExpLoadIAS`, `flaggInv`, `flaggLoan`, `flaggLoanName`, `flaggMeasures`, `flaggOtherFund`, `flaggOtherFundName`, `flaggReasons`, `flaggStatus`, `flarepCode`) VALUES ("UKENTH_Agg000052","CROSSNESS",240568,"Financial support in place for the Tideway Tunnel and site preparation beginning. Investment figures are in Euros based on a 1 £ sterling to 1.13 Euros conversion.",0.0000000000,"na","07/01/23 00:00:00","12/01/06 00:00:00","08/01/10 00:00:00",1604197,100.0000000000,0.0000000000,5729.0000000000,NULL,NULL,"Improved sewage treatment at the works both in terms of the volume that can be treated and the quality of the effluent. Expanded by 46%, new permit 2 years ago. Tideway Tunnel construction to capture the flows that would previously have discharged to river and conveying them to Crossness Sewage Treatment Works for full treatment. See note above on progress with the tunnel.",NULL,NULL,"Significant proportion of effluent not passing through full treatment instead discharging via storm overflows","NC","UK5");
INSERT INTO `T_Art17_FLAAgglomeration` (`aggCode`, `aggName`, `Art17_FLAaggloID`, `flaggComments`, `flaggEUFund`, `flaggEUFundName`, `flaggExpecDateCompletion`, `flaggExpecDateStart`, `flaggExpecDateStartWork`, `flaggExpLoad`, `flaggExpLoadColl`, `flaggExpLoadIAS`, `flaggInv`, `flaggLoan`, `flaggLoanName`, `flaggMeasures`, `flaggOtherFund`, `flaggOtherFundName`, `flaggReasons`, `flaggStatus`, `flarepCode`) VALUES ("UKENNE_Agg000028","HENDON",240569,"Investment figures are in Euros based on a 1 £ sterling to 1.13 Euros conversion.",0.0000000000,"na","12/01/17 00:00:00","04/01/13 00:00:00","11/01/16 00:00:00",197491,100.0000000000,0.0000000000,8.0000000000,NULL,NULL,"Works have been underway and proposals were discussed with the Commission. The works were completed in December 2017 with spill data reported to the Commission every 6 months.",NULL,NULL,"The frequency of discharge via storm overflows is considered too large.","NC","UK5");
